<?php Class Holiday extends Auth_Controller  { 	var $data = array();		public  function __construct(){		error_reporting(0);		parent::__construct();		$this->load->model('holiday_model');		$this->load->helper('url');		$this->load->library('session');  		$this->load->helper(array('form', 'url'));  		$this->has_permission(178);		$this->data = $this->get_privs();	} 		public function index($action = '', $break_type = '', $break_action = ''){		$this->load->helper('form');		$this->set_header_data(PROJECT_NAME);					$data['user_type'] = $this->session->userdata('user_type');		$data['session'] = $this->session->all_userdata();		$data['privs'] = $this->data;		$data['all_userdata'] = $this->session->all_userdata();		$data['lu_holiday_type'] = $this->getLookup('holiday_type',1);								$this->load->view('holiday/index',$data);					$this->load->view('templates/footer'); 		}		public function save(){		$this->holiday_model->save();		header('location:'.base_url() . 'holiday');		exit();	}		public function lw(){		$data['user_type'] = $this->session->userdata('user_type');		$data['lu_holiday_type'] = $this->getLookup('holiday_type',1);				$data['holiday_list'] = $this->db->select('*')										->from('holidays')										->where('is_active',1)										->order_by('target_date')										->get()->result_array();												$this->load->view('holiday/list', $data);			}		public function del($record){		$this->db->update('holidays',array('is_active'=>0),array('id'=>$record));		header('location:'.base_url() . 'holiday');		exit();	}	}?>