<div class='page-header'>users list</div><input type='checkbox' id='chk-active-user' <?=($showAll=='1') ?'checked=checked':''?>><span class='note' >Show all users</span><div id='div-users'><table>	<tr>		<td valign=top>active</td>		<td valign=top>user id</td>		<td valign=top>user name</td>		<td valign=top>type</td>		<td valign=top>			login time 			<br>			<span class='note'>Apply to all inputs below:</span><br>			<input style='border:1px solid red;'  type='input' id='main_target_login' placeholder='hh:mm' maxlength=5 size=10>			<br><br>		</td>		<td></td>	</tr>	<?foreach($users as $d){?>	<tr>				<td>			<?if(isset($privs[182])){?>					<input type='checkbox' class='chk-active-ctrl-user' username='<?=$d['user_name']?>'  <?=($d['is_active']=='1') ?'checked=checked':''?>>			<?}?>		</td>		<td><?=$d['user_name']?></td>		<td><?=$d['firstname']. ' ' . $d['lastname']?></td>		<td><?=$userTypes[$d['user_type']]?></td>		<td>			<?if(isset($privs[182])){?>					<input type='input' placeholder='hh:mm' class='target_login' username='<?=$d['user_name']?>'  value = '<?=substr($d['target_login'],0,5)?>' maxlength=5 size=10>			<?}?>		</td>		<td>			<?if(isset($privs[181])){?>			<a class= 'a-edit-user' href='<?=base_url()?>users/edit/<?=$d['user_name']?>'>edit</a>			<?}?>		</td>	</tr>		<?}?></table></div><script>	$(document).ready(function(){	    $("#chk-active-user").click(function(){            var val = $(this).prop('checked');            var showInActive = 0;            if(val == true)                showInActive = 1;            window.location ='<?=base_url()?>users/index/'+showInActive;        });        $('.chk-active-ctrl-user').click(function(){            var username = $(this).attr('username');            var val = $(this).prop('checked');            var active  = 0;            if(val == true)                active = 1;            var data ={};            data['user_name'] =username;            data['is_active'] =active;            $.ajax({                url:'<?=base_url()?>users/active/',                data:data,                type:'POST',                success:function(data){                   // alert(data);                }            })        })		$(".a-edit-user").click(function(){			$.ajax({				url:$(this).prop('href'),				success:function(data){					$("#div-users").html(data);				}			})			return false;		})				$('.target_login').change(function(){			var val = $(this).val();            var username = $(this).attr('username');						var data ={};            data['user_name'] = username;            data['target_login'] = val;            $.ajax({                url:'<?=base_url()?>users/set_target_login/',                data:data,                type:'POST',                success:function(data){                   // alert(data);                }            })		})				$('#main_target_login').change(function(){			//apply to all "target_login" fields			$('.target_login').val($(this).val());			$('.target_login').change();		})			})</script>